// ProductDetails.js

import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { useStateContext } from "../context/ShopContext";
import StarRating from "./StarRating";
import { Link } from "react-router-dom";
const ProductDetails = () => {
  const { id } = useParams(); // Get the 'id' parameter from the URL
  console.log(id);
  const { data, addToCart, handleOpen } = useStateContext();
  const [userRating, setUserRating] = useState(0);
  const handleBuy = (id) => {
    addToCart(id);
    handleOpen();
  };
  const handleRatingChange = (newRating) => {
    // Update the user's rating in the state or perform any other actions you want with the new rating.
    setUserRating(newRating);
  };
  // Find the product details using the 'id'
  const product = data.find((product) => product.webID === id);
  // Handle the case when the product is not found
  if (!product) {
    return <div>Product not found!</div>;
  }

  // Display the product details
  return (
    <div>
      <div className="flex flex-col text-center justify-center  items-center mt-10">
        <img
          className="w-[20rem] h-[20rem] "
          src="data:image/jpeg;base64,"
          // src={product.img} // Use the 'img' property from the product object
          alt={product.name} // Use the 'name' property from the product object
        />
        <p>{product.name}</p>
        <p>{product.productTitle}</p>
        <p>Price: {product.price}$</p>
        {/* Add other product details you want to display */}
        <StarRating
          initialRating={userRating}
          onRatingChange={handleRatingChange}
        />
        <Link to="/">Continue shopping </Link>
        <button onClick={() => handleBuy(id)}>Buy now</button>
      </div>
    </div>
  );
};

export default ProductDetails;
